classes.content:
self {
  border-radius: 8px;
  box-shadow: 0 0 40px 0 rgb(0 0 0 / 15%);
	border-width: 1px;
}

self .header .header-cell {
  background-color: transparent;
}

self .header-cell {
  background-color: transparent;
	color: #fff;
  font-weight: bold;
  padding: 0rem 0.5rem;
}

self .header {
  background-image: linear-gradient(to right, #cc75ca , #6a36ca);
}

self .row {
  align-items: center;
}

self .row .cell {
  padding: 0rem 0.5rem;
}

self .row .cell > span {
  justify-content: flex-start;
}

self .row.selected {
  background-color: #d2d7f5;
}

self .row-even:not(.selected):not(:hover) {
  background-color: #f8f6ff;
}

self .header .resizable > :not([hidden]) ~ :not([hidden]),
self .row > :not([hidden]) ~ :not([hidden]),
self .Grid .innerScrollContainer,
self .Grid .innerScrollContainer > :not([hidden]) ~ :not([hidden]) {
  border-width: 0;
}

self {
	cursor: pointer;
  background-image: linear-gradient(to right, #cc75ca , #6a36ca);
	width: 108px;
}
self:hover {
	opacity: 0.8;
}
self:active {
	opacity: 1;
}
